    long getStart()
    {
        return start;
    }

    long getEnd()
    {
        return end;
    }

    private void setRange(String s, String e) {
        start=Long.parseLong(s);
        end=Long.parseLong(e);
    }

    private boolean checkRange() {
        if(end>start)
            return true;
        else return false;
    }

    void setStartTf(String startRange) {
        startTF.setText(startRange);
    }

    void setEndTf(String endRange) {
        endTF.setText(endRange);
    }
    
    String getStartStr()
    {
        return startTF.getText();
    }
    
    String getEndStr()
    {
        return endTF.getText();
    }
